mapper.select:
SELECT id,title,content,user_name,user_email,send_status,reply_status,create_time,update_time,FORMATDATETIME(create_time,'yyyy-MM-dd HH:mm:ss') as create_time_format
  FROM msg
 WHERE id = #{id}

