mapper.update:
    UPDATE product 
    	SET update_time = now()
			,name = #{name,jdbcType=VARCHAR}
    		,sku = #{sku,jdbcType=VARCHAR}
    		,image = #{image,jdbcType=VARCHAR}
    		,rich_text = #{richText,jdbcType=VARCHAR}
    WHERE id = #{id,jdbcType=INTEGER}

UPDATE product 
	SET qrcode = #{qrcode,jdbcType=VARCHAR}
WHERE id = #{id,jdbcType=INTEGER}
